
public class P4_HardyTrent_Sum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("P4_HardyTrent_Sum");	
		System.out.println("Call value a: 16 *** Call value b: 15");
		System.out.println("*******");
		System.out.println("Call value a: 15 *** Call value b: 15");
		System.out.println("Method sumDouble Result: " + sumDouble(15,15));
	}

		public static int sumDouble(int a, int b) {
			  int sum = 0;
			  if (a == b) {
			    return sum = (a + b) * 2;
			  }
			  return sum = (a + b);
			 }
	}